CREATE TABLE IF NOT EXISTS User (id INTEGER PRIMARY KEY, rawLogin TEXT, login TEXT, name TEXT, bio TEXT, email TEXT, avatar_url TEXT, blog TEXT, company TEXT, location TEXT, collaborators INTEGER, public_repos INTEGER, owned_private_repos INTEGER, public_gists INTEGER, private_gists INTEGER, followers INTEGER, following INTEGER, disk_usage INTEGER);
CREATE TABLE IF NOT EXISTS Repository (userId INTEGER, id INTEGER, name TEXT, owner_login TEXT, description TEXT, language TEXT, pushed_at TEXT, created_at TEXT, updated_at TEXT, clone_url TEXT, ssh_url TEXT, git_url TEXT, html_url TEXT, default_branch TEXT, private INTEGER, fork INTEGER, watchers_count INTEGER, forks_count INTEGER, stargazers_count INTEGER, open_issues_count INTEGER, subscribers_count INTEGER, isStarred INTEGER, PRIMARY KEY (userId, id));